    public static void ActualizarTablero(){
        
        for(int x = 0;x<3;x++){
            for(int y = 0;y<3;y++){
                String actual = tablero[x][y];
                switch (actual) {
                    case "X":
                        tablero[x][y] = "Equis";
                        break;
                    case "J":
                        tablero[x][y] = "Jota";
                        break;
                    case "B":
                        tablero[x][y] = "Bueno";
                        break;
                    default:
                        tablero[x][y] = "Locaaaa";
                        break;
                }
            }
        }
        
        for(int x = 0; x<3; x++){
            System.out.println("Fila: " + x);
            for(int y = 0;y<3;y++){
                System.out.println("Columna: " + y);
                System.out.println("Actual: "+tablero[x][y]);
            }
        }
    }
    public static void InicializarTablero(){
        for(int x = 0;x<3;x++){
                for(int y = 0;y<3;y++){
                    tablero[x][y] = "X";
                }
            }
        tablero[1][1] = "J";
        tablero[2][2] = "B";
        ActualizarTablero();
        
    }
    public static String[][] tablero = new String[3][3];
